mapper.select:
select *
from TIME_TABLE
where TEATER_NUM = #{theaterNum} and  MOVIE_NUM = #{theaterNum}

select *
from Screen
where SCREEN_NAME = #{screenName}  and  TEATER_NUM = #{teaterNum} and MOVIE_NUM = #{movieNum}

select *
from TIME_TABLE
where TIME_START = #{timeStart} and TEATER_NUM = #{teaterNum} and MOVIE_NUM = #{movieNum} and SCREEN_NAME = #{screenName}
